import React from 'react';
import styled from 'styled-components';

const HeroSection = styled.section`
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #76c893 10%, #9575cd 100%);
  color: white;
  text-align: center;
`;

const HeroTitle = styled.h1`
  font-size: 3.5rem;
  margin: 0;
`;

const HeroSubtitle = styled.p`
  font-size: 1.2rem;
  margin: 15px 0;
`;

const HeroButton = styled.a`
  padding: 10px 30px;
  background-color: #1a1a1a;
  color: white;
  text-decoration: none;
  border-radius: 5px;
  font-size: 1.2rem;
  cursor: pointer;
  margin-top: 20px;

  &:hover {
    background-color: #333333;
  }
`;

const Hero = () => {
  return (
    <HeroSection id="home">
      <HeroTitle>SkyLabs Developments</HeroTitle>
      <HeroSubtitle>Imagination is our only limitation. Development is agile. Creation is art.</HeroSubtitle>
      <HeroButton href="#features">Explore Features</HeroButton>
    </HeroSection>
  );
};

export default Hero;
